(function(e){
  "use strict";
  e.fn.counterUp=function(t){
    var n=e.extend({time:400,delay:10},t);
    return this.each(function(){
      var t=e(this),r=n,
      i=function(){
        var e=[],n=r.time/r.delay,
            original = t.text().trim(),
            match = original.match(/^([\d\.,]+)(.*)$/),  // split number + suffix
            number = match ? match[1].replace(/,/g,"") : original,
            suffix = match ? match[2] : "",
            s=/[0-9]+,[0-9]+/.test(number),
            o=/^[0-9]+$/.test(number),
            u=/^[0-9]+\.[0-9]+$/.test(number),
            a=u?(number.split(".")[1]||[]).length:0;

        for(var f=n;f>=1;f--){
          var l=parseInt(number/n*f);
          u && (l=parseFloat(number/n*f).toFixed(a));
          if(s) while(/(\d+)(\d{3})/.test(l.toString())) 
            l=l.toString().replace(/(\d+)(\d{3})/,"$1,$2");
          e.unshift(l+suffix); // add suffix to every step
        }

        t.data("counterup-nums",e);
        t.text("0"+suffix);

        var c=function(){
          t.text(t.data("counterup-nums").shift());
          if(t.data("counterup-nums").length)
            setTimeout(t.data("counterup-func"),r.delay);
          else{
            delete t.data("counterup-nums");
            t.data("counterup-nums",null);
            t.data("counterup-func",null);
          }
        };
        t.data("counterup-func",c);
        setTimeout(t.data("counterup-func"),r.delay);
      };
      t.waypoint(i,{offset:"100%",triggerOnce:!0});
    });
  }
})(jQuery);


// Init
jQuery(document).ready(function($) {
  $('.counter').counterUp({
    delay: 10,
    time: 1000
  });
});
